import React, { useState } from 'react';
import { Button, TextField, Grid } from '@mui/material';

const SubmitButton = ({ submittable }) => {
  return (
    <Button variant="contained" type="submit" disabled={!submittable}>
      Submit
    </Button>
  );
};

const App = () => {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [address, setAddress] = useState('');
  const [submittable, setSubmittable] = useState(false);

  const handleFormValidation = () => {
    // Add validation logic here for each field
    const isNameValid = name.trim().length > 0;
    const isAgeValid = !isNaN(age) && Number(age) > 0;
    const isAddressValid = address.trim().length > 0;

    // Enable/disable the submit button based on field validations
    setSubmittable(isNameValid && isAgeValid && isAddressValid);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
    if (submittable) {
      // Perform submission logic
      console.log(name);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <Grid container spacing={2} direction="column">
        <Grid item>
          <TextField
            label="Name"
            value={name}
            onChange={(e) => {
              setName(e.target.value);
              handleFormValidation();
            }}
            required
          />
        </Grid>
        <Grid item>
          <TextField
            label="Age"
            value={age}
            onChange={(e) => {
              setAge(e.target.value);
              handleFormValidation();
            }}
            required
            type="number"
          />
        </Grid>
        <Grid item>
          <TextField
            label="Address"
            value={address}
            onChange={(e) => {
              setAddress(e.target.value);
              handleFormValidation();
            }}
            required
          />
        </Grid>
        <Grid item>
          <Grid container spacing={2} justifyContent="flex-start">
            <Grid item>
              <SubmitButton submittable={submittable} />
            </Grid>
            <Grid item>
              <Button type="reset" variant="outlined">
                Reset
              </Button>
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </form>
  );
};

export default App;
